data.contents:
.svg-container {
  display: inline-block;
  background: url("@{svg}") top left no-repeat;
  background-size: 100% 100%;

  & > summary {
    width: 200px;
    height: 200px;
    font-size: 0; /* hide the arrow */
  }

  .inner-spacer {
    height: 1px;
  }
}

/*
 in several browsers, animation does not work inside
 svgs loaded as an image unless you have something
 animating constantly.
 if you don't need animation, you can remove this to
 save resources!
 */
.animation-fix {
  position: fixed;
  top: 0;
  left: 0;
  width: 1px;
  height: 1px;
  animation: animation-fix 1s infinite;
}
@keyframes animation-fix {
  100% { transform: rotate(360deg) }
}

.container {
  font-family: sans-serif;
}

.click-prompt {
  position: absolute;
  width: 200px;
  height: 200px;
  padding: 1em;
  box-sizing: border-box;
  background: black;
  color: white;
  transition: all 1s;
}
.contents {
  position: relative;
  width: 200px;
  height: 200px;
  padding: 1em;
  box-sizing: border-box;
  background: #4a0;
  color: white;
  transition: all 1s;
  transform: translateY(100%);
}

/*
 here we define what happens when the container is clicked,
 i.e. when it's taller than 200px
 */
@media (min-height: 201px) {
  .click-prompt {
    transform: scale(0);
  }
  .contents {
    transform: none;
  }
}
